function checkSubmit() {
    console.log('Clickeando');
    $('input[type="text"]').keypress(function() {
        if ($(this).val() != '') {
            $('button[type="submit"]').removeAttr('disabled');
        }
    });
    $('button[type="submit"]').attr('disabled', 'disabled');
    return true;
}
